import React from "react";
import PropTypes from "prop-types";

class EditFishForm extends React.Component {

static PropTypes = {
  fish: PropTypes.string,
  updateFish: PropTypes.number,
  index: PropTypes.string,
  fishes: PropTypes.number,
  deleteFish: PropTypes.number,
};

    handleChange = event => {
      console.log(event.currentTarget.value);
      // Take a copy of the current fish
      const updatedFish = {
        ...this.props.fish,
        [event.currentTarget.name]: event.currentTarget.value
      };
      this.props.updateFish(this.props.index, updatedFish);
    };
    render() {
        return (
        <div className="fish-edit">
            <input type="text" 
            name="name" 
            onChange={this.handleChange} 
            value={this.props.fishes.name} 
            />
            <input type="text" 
            name="price" 
            onChange={this.handleChange} 
            value={this.props.fishes.price} 
            />
            <select 
            type="text" 
            name="status" 
            onChange={this.handleChange} 
            value={this.props.fishes.status} 
            >
                <option value="available">Fresh!</option>
                <option value="unavailable">Sold Out!</option>
            </select>
            <textarea 
            name="desc" 
            onChange={this.handleChange} 
            value={this.props.fishes.desc} 
            />
            <input 
            type="text" 
            name="image" 
            onChange={this.handleChange} 
            value={this.props.fishes.image} 
            />
            <button onClick={() => this.props.deleteFish(this.props.index)}>Remove Fish</button>
        </div>
        );
    }
}

export default EditFishForm;
